import React from 'react';

function App(){
    let newDateTime = new Date();
    let hours = newDateTime.getHours();
    let mins = newDateTime.getMinutes();
    let secs = newDateTime.getSeconds();
    let timeZone = newDateTime.toLocaleString('en-US', { hour: 'numeric', hour12: true })
    
    return(
        <>
            <div className='mainWrapper'>
            <div className='innerWrapper'>
                <div className='bar'>
                <p>{hours}:{mins}:{secs} {timeZone}</p>
                </div>
            </div>
            </div>
        </>
    )
}

export default App;
